    document.addEventListener(
      'mouseup',
      mouseHandlerWrapper(() => this.setState({ dragging: false, activeIndex: -1 }))
    );
  }

  render() {
    const { scale, steps, theme, ...props } = this.props;

    const { min, max, step, sliderVal, logVal, scaleValue } = (scale === 'log'
      ? getLogDisplayOpts
      : getNormalDisplayOpts)(props);
    validateStepParams(props.step, steps);

    // These values need to be available for the global mouse event listeners, so we set them
    // as a class property.  This doesn't trigger re-renderings since we're not using `setState`.
    this.computedScaleInfo = {
      value: logVal,
      min,
      max,
      step: isnumeric(steps) ? (max - min) / steps : step,
      scaleValue,
    };

    return (
      <React.Fragment>
        <span
          className="control-panel-interval"
          style={{ backgroundColor: theme.background2 }}
          ref={this.input}
          onMouseDown={evt => {
            this.setState({ dragging: true });

            // Figure out which side is being clicked and adjust the active index accodingly
            const { fraction, hifrac, lofrac } = this.calculateFraction(evt, sliderVal, min, max);

            // This is just for making decisions, so perturb it ever so slightly just in case the
            // bounds are numerically equal.
            const lodiff = Math.abs(lofrac - Math.abs(max - min) * 1e-15 - fraction);
            const hidiff = Math.abs(hifrac + Math.abs(max - min) * 1e-15 - fraction);

            // Determine which one is closer
            this.setState({ activeIndex: lodiff < hidiff ? 0 : 1 });
          }}
        >
          <span
            className="control-panel-interval-handle"
            style={{
              left: ((sliderVal[0] - min) / (max - min)) * 100 + '%',
              right: 100 - ((sliderVal[1] - min) / (max - min)) * 100 + '%',
              backgroundColor: theme.foreground1,
            }}
          />
        </span>
        <Value text={logVal[0]} width="11%" left={true} />
        <Value text={logVal[1]} width="11%" />
      </React.Fragment>
    );
  }
}

export default withErrorHandler(withSettingState()(Range));
import React from 'react';

import { compose } from '../util';
import { withSettingState } from './context';
import { withErrorHandler } from '../error';

const CustomComponentInner = ({ Comp, renderContainer, ...props }) => <Comp {...props} />;

/**
 * Takes a prop, `Comp`, which is a component that will be rendered as a control panel input.
 *
 * If the `renderContainer` prop is `true` (default), a `Container` will be rendered as the parent
 * of this element.  That gives you the label on the left and assumes your styling is going to be
 * pretty close to that of the rest of control-panel's imputs.  If you have a highly custom
 * component, you may want to instead handle that all yourself in which you should pass
 * `renderContainer={false}` to `CustomComponent`.
 *
 * The `Comp` child will be passed the following props:
 * - `theme`
 * - `value`
 * - `onChange`
 */
const CustomComponent = compose(
  withSettingState(),
  withErrorHandler
)(CustomComponentInner);

export default CustomComponent;
import React from 'react';
import isstring from 'is-string';
import PropTypes from 'prop-types';

import themes from './themes';
import Title from './components/title';
import ControlPanelContext from './components/context';
import Button from './components/button';
import Checkbox from './components/checkbox';
import Multibox from './components/multibox';
import Select from './components/select';
import Text from './components/text';
import Color from './components/color';
import Range from './components/range';
import Interval from './components/interval';
import Custom from './components/custom';
import { createPolyProxy } from './util';
import './components/styles/base.css';
import './components/styles/color.css';

export { default as Button } from './components/button';
export { default as Checkbox } from './components/checkbox';
export { default as Multibox } from './components/multibox';
export { default as Select } from './components/select';
export { default as Text } from './components/text';
export { default as Color } from './components/color';
export { default as Range } from './components/range';
export { default as Interval } from './components/interval';
export { default as Custom } from './components/custom';
export { default as themes } from './themes';
export { default as Value } from './components/value';
export { Container, withSettingState, withTheme, Label } from './components/context';

const settingTypeMapping = {
  range: Range,
  text: Text,
  checkbox: Checkbox,
  color: Color,
  button: Button,
  select: Select,
  multibox: Multibox,
  interval: Interval,
  custom: Custom,
};

const VALID_POSITIONS = ['top-right', 'top-left', 'bottom-right', 'bottom-left'];

/**
 *
 * @param {string | { left?: number, right?: number, bottom?: number, top?: number }} position
 */
const parsePositionPropToOffsets = position => {
  if (typeof position === 'object') {
    return position;
  }

  if (!position) {
    return {};
  }

  return {
    ...(position.includes('top') ? { top: 8 } : {}),
    ...(position.includes('left') ? { left: 8 } : {}),
    ...(position.includes('right') ? { right: 8 } : {}),
    ...(position.includes('bottom') ? { bottom: 8 } : {}),
  };
};

class ControlPanel extends React.Component {
  constructor(props) {
    super(props);

    const position = parsePositionPropToOffsets(props.position);
    this.state = { data: props.initialState || props.state || {}, position };
    this.derivedSettings = [];

    if (!this.props.settings) {
      return;
    }

    const { derivedInitialState } = this.computeDerivedSettings(this.props.settings);
    this.state.data = { ...this.state.data, ...derivedInitialState };
  }

  lastSettings = null;
  computeDerivedSettings(settings = []) {
    if (this.lastSettings === settings) {
      return { derivedSettings: this.derivedSettings };
    }
    this.lastSettings = settings;

    const { derivedSettings, derivedInitialState } = settings.reduce(
      ({ derivedInitialState, derivedSettings }, { type, label, initial, ...props }) => {
        const SettingComponent = settingTypeMapping[type];

        if (!SettingComponent) {
          return { derivedInitialState, derivedSettings };
        }
        return {
          derivedInitialState: { ...derivedInitialState, [label]: initial },
          derivedSettings: [...derivedSettings, { SettingComponent, label, props }],
        };
      },
      { derivedInitialState: {}, derivedSettings: [] }
    );
    this.derivedSettings = derivedSettings;
    return { derivedSettings, derivedInitialState };
  }

  componentDidMount() {
    if (this.props.draggable) {
      document.addEventListener('mousemove', this.handleMouseDrag);
      // `setTimeout` here in order to avoid re-rendering the component and potentially discarding
      // new data from child settings that were changed as a result of this.
      document.addEventListener('mouseup', () =>
        setTimeout(() => this.setState({ dragging: false }))
      );
    }

    if (!this.props.contextCb) {
      return;
    }

    const handler = {
      get: (state, prop) => this.state.data[prop],
      set: (state, prop, val) => this.setState({ data: { ...this.state.data, [prop]: val } }),
    };

    const setData = data => this.setState({ data: { ...this.state.data, ...data } });
    this.props.contextCb(createPolyProxy(this.state.data, handler, setData));
  }

  getState() {
    return this.props.state ? this.props.state : this.state.data;
  }

  indicateChange(label, newVal) {
    const newState = { ...this.getState(), ...this.state.data, [label]: newVal };
    this.props.onChange(label, newVal, newState);
    if (!this.props.state) {
      this.setState({ data: newState });
    }
  }

  handleMouseDown = evt => {
    if ((evt.target.className || '').includes('draggable')) {
      this.setState({
        dragging: true,
        mouseDownCoords: { x: evt.pageX, y: evt.pageY },
        mouseDownPos: { ...this.state.position },
      });
    }
  };

  handleMouseDrag = evt => {
    if (this.state.dragging) {
      const diffX = evt.pageX - this.state.mouseDownCoords.x;
      const diffY = evt.pageY - this.state.mouseDownCoords.y;

      const position = (typeof this.props.position === 'string' ? this.props.position : '') || '';
      const offset = position.includes('left')
        ? { left: this.state.mouseDownPos.left + diffX }
        : { right: this.state.mouseDownPos.right - diffX };

      const newPosition = { ...this.state.position, ...offset };
      if (this.state.mouseDownPos.top !== undefined) {
        newPosition.top = this.state.mouseDownPos.top + diffY;
      } else if (this.state.mouseDownPos.bottom !== undefined) {
        newPosition.bottom = this.state.mouseDownPos.bottom - diffY;
      }

      this.setState({ position: newPosition });
    }
  };

  render() {
    const {
      width,
      theme: suppliedTheme,
      position = '',
      title,
      children,
      style,
      settings,
    } = this.props;

    const theme = isstring(suppliedTheme) ? themes[suppliedTheme] || themes['dark'] : suppliedTheme;
    const state = this.getState();
    const combinedStyle = {
      display: 'inline-block',
      background: theme.background1,
      width,
      padding: 14,
      paddingBottom: 8,
      opacity: 0.95,
      position:
        VALID_POSITIONS.includes(position) || typeof position !== 'string' ? 'absolute' : undefined,
      ...(this.state.position || {}),
      cursor: this.props.draggable ? 'move' : undefined,
      ...style,
    };

    const { derivedSettings } = this.computeDerivedSettings(settings);

    return (
      <div
        className="control-panel draggable"
        onMouseDown={this.props.draggable ? this.handleMouseDown : undefined}
        style={combinedStyle}
      >
        <ControlPanelContext.Provider
          value={{
            state,
            theme,
            indicateChange: this.indicateChange.bind(this),
          }}
        >
          {title ? <Title title={title} /> : null}
          {children}
          {derivedSettings.map(({ SettingComponent, label, props }) => (
            <SettingComponent
              key={label}
              label={label}
              {...props}
              value={state[label]}
              onChange={newVal => this.setState({ data: { ...this.state.data, [label]: newVal } })}
            />
          ))}
        </ControlPanelContext.Provider>
      </div>
    );
  }
}

ControlPanel.propTypes = {
  initialState: PropTypes.object,
  onChange: PropTypes.func,
  theme: PropTypes.oneOfType([PropTypes.string, PropTypes.object]),
  title: PropTypes.string,
  width: PropTypes.number,
  position: PropTypes.oneOfType([PropTypes.oneOf(VALID_POSITIONS), PropTypes.string]),
  style: PropTypes.object,
  settings: PropTypes.arrayOf(PropTypes.object),
  state: PropTypes.object,
  contextCb: PropTypes.func,
  draggable: PropTypes.bool,
};

ControlPanel.defaultProps = {
  width: 300,
  theme: 'dark',
  onChange: () => {},
  style: {},
};

export default ControlPanel;
